const StarRating = ({ stars }) => (
  <div className="text-yellow-500">
    {"★".repeat(stars)}
    {"☆".repeat(5 - stars)}
  </div>
);

const Men = () => {
  const navigate = useNavigate();

  const renderSection = (title, items) => (
    <div className="mb-10">
      <h2 className="text-2xl font-bold text-center text-blue-700 mb-6">{title}</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
        {items.map((item) => (
          <div
            key={item.id}
            onClick={() => navigate(`/men/${item.id}`)}
            className="bg-white shadow-md rounded-lg p-4 flex flex-col items-center cursor-pointer hover:shadow-xl"
          >
            <img
              src={item.image}
              alt={item.name}
              className="w-full h-40 object-cover rounded mb-2"
            />
            <h3 className="text-lg font-semibold text-center">{item.name}</h3>
            <p className="text-gray-600 mb-2">{item.price}</p>
            <button className="bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-700">
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div className="bg-gradient-to-b from-blue-50 to-white py-10 px-4 min-h-screen">
      {/* Intro Description */}
      <div className="max-w-4xl mx-auto text-center mb-10">
        <h1 className="text-3xl font-bold text-blue-800 mb-4">Men's Collection</h1>
        <p className="text-gray-700 text-lg">
          Discover premium quality men’s fashion including casual wear, formals, accessories,
          and more. Tailored to your lifestyle and comfort.
        </p>
      </div>

      {/* Product Sections */}
      {renderSection("Featured Men's Items", featuredItems)}
      {renderSection("Exclusive Men's Items", exclusiveItems)}
      {renderSection("Men's Offer Price Items", offerItems)}

      {/* Testimonials */}
      <div className="mt-20 bg-white py-10 rounded-2xl shadow-md max-w-6xl mx-auto px-6">
        <h2 className="text-2xl font-bold text-blue-700 text-center mb-8">💬 What Our Customers Say</h2>
        <div className="grid md:grid-cols-3 gap-6">
          {testimonials.map((review, index) => (
            <div key={index} className="bg-blue-50 p-4 rounded-xl shadow text-sm space-y-2">
              <p className="font-semibold">{review.name}</p>
              <StarRating stars={review.stars} />
              <p className="text-gray-600">{review.comment}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Men;
